import React from "react";
import ReactDOM from "react-dom";

const App = () => {
  const course = "Half Stack application development";
  const parts = [
    {
      name: "Fundamentals of React",
      exercises: 10,
    },
    {
      name: "Using props to pass data",
      exercises: 7,
    },
    {
      name: "State of a component",
      exercises: 14,
    },
  ];

  const Header = (props) => {
    return (
      <div>
        <h1>{props.course}</h1>
      </div>
    );
  };

  const Part = (props) => {
    return (
      <div>
        <p>
          {props.name} {props.exercises}
        </p>
      </div>
    );
  };

  const Content = (props) => {
    return (
      <div>
        <Part name={props[0].name} exercises={props[0].exercises} />
        <Part name={props[1].name} exercises={props[1].exercises} />
        <Part name={props[2].name} exercises={props[2].exercises} />
      </div>
    );
  };

  const Total = (props) => {
    return (
      <div>
        <p>
          Number of exercises{" "}
          {props[0].exercises + props[1].exercises + props[2].exercises}
        </p>
      </div>
    );
  };

  return (
    <div>
      <Header name={course} />
      <Content props={parts} />
      <Total props={parts} />
    </div>
  );
};

ReactDOM.render(<App />, document.getElementById("root"));
